mapper.select:
select distinct t.tour_id, t.title, t.sub_heading, t.content, t.created_date, t.total_hour, t.hash_tag, t.personnel, t.transportation, t.price, t.city_id, t.location, ci.city_id, ci.city_name, co.country_id, co.country_name, co.continent , count(member_id) as cnt , count(fr.reservation_id) as cnt from tour as t join city as ci on t.city_id = ci.city_id join country as co on ci.country_id = co.country_id join tour_theme as toth on t.tour_id =toth.tour_id join theme as th on toth.theme_id = th.theme_id left outer join member_tour_wishlist as mtw on t.tour_id = mtw.tour_id
left outer join reservation as r on t.tour_id = r.tour_id left outer join free_review as fr on r.reservation_id = fr.reservation_id
or continent = #{continentName} and country_name = #{countryName} and city_name = #{cityName} and price between #{minPrice} and #{maxPrice} and total_hour between #{minHour} and #{maxHour}
and th.theme in #{theme}
order by price asc order by price desc order by tour_id desc group by t.tour_id order by cnt desc group by t.tour_id order by cnt desc
limit #{rowNo}, #{size}

select distinct city_name from member_tour_wishlist as m
join tour as t on m.tour_id = t.tour_id
join city as c on t.city_id = c.city_id where member_id = #{memberNo}

mapper.delete:
delete from
member_tour_wishlist where member_id = #{memberNo} and tour_id =
#{tourNo}
